import moment from 'moment';
var user = undefined
export function getNews() {
  return new Promise(async function (resolve, reject) {
    const imageDirectory = "/res/news"
    news = [
      { id: 0, image: imageDirectory + "carousel_a4_image", title: "Audi A4 available in Garage", subtitle: "Try the new Audi A4, available from 06/08" },
      { id: 1, image: imageDirectory + "carousel_promo_image", title: "Special promo", subtitle: "Complete 3 rentals and get 10% off on future rentals." },
      { id: 2, image: imageDirectory + "carousel_giulia_image", title: "Alfa Romeo Giulia available in garage", subtitle: "TTry Alfa Romeo Giulia, available from 05/02." }
    ]
    resolve(news)
  });
}
export function deleteRental(rental) {
  return new Promise(async function (resolve, reject) {
    try {
      const response = await fetch(`/api/rental/${rental.id}`,
        {
          method: 'DELETE'
        });
      if (!response.ok) {
        reject();
      }
      resolve();
    } catch (e) {
      reject();
    }
  });
}
export function carList() {
  return new Promise(async function (resolve, reject) {
    const carsDirectory = "/res/cars"
    cars = [
      { id: 0, brand: "Audi", model: "A1", image: "a1_image.jpg", segment: 3 },
      { id: 1, brand: "Audi", model: "A1", image: "a1_image.jpg", segment: 3 },
      { id: 2, brand: "Audi", model: "A4", image: "a4_image.jpg", segment: 2 },
      { id: 3, brand: "Alfa Romeo", model: "Giulia", image: "giulia_image.jpg", segment: 2 },
      { id: 4, brand: "Alfa Romeo", model: "Giulia", image: "giulia_image.jpg", segment: 2 },
      { id: 5, brand: "Mercedes", model: "A-Class", image: "aclass_image.jpg", segment: 2 },
      { id: 6, brand: "Mercedes", model: "C-Class", image: "cclass_image.jpg", segment: 1 },
      { id: 7, brand: "Mercedes", model: "C-Class", image: "cclass_image.jpg", segment: 1 },
      { id: 8, brand: "Fiat", model: "Panda", image: "panda_image.jpg", segment: 5 },
      { id: 9, brand: "Fiat", model: "Panda", image: "panda_image.jpg", segment: 5 },
    ]
    resolve(cars)
  });
}
export function addRental(rental) {
  return new Promise(async function (resolve, reject) {
    try {
      const response = await fetch('/api/add', {
        method: 'put',/* ERRORE: è una POST */
        headers: {
          'Content-type': 'application/json'
        },
        body: JSON.stringify(rental)
      });
      const completeRental = await response.json();
      if (!response.ok)
        reject();
      resolve({ ...completeRental, startDate: moment(completeRental.startDate, 'YYYY-MM-DD'), endDate: moment(completeRental.endDate, 'YYYY-MM-DD') });
    } catch (e) {
      reject();
    }
  });
}
export function getPrices(category, startDate, endDate) {
  return new Promise(async function (resolve, reject) {
    try {
      const response = await fetch('/api/prices');
      const prices = await response.json();
      if (!response.ok)
        reject();
      resolve(prices);
    } catch (e) {
      reject();
    }
  });
}
let rentals = [];
export function getAvailableCars(startDate, endDate, category) {
  return new Promise(async function (resolve, reject) {
    try {
      const response = await fetch('/api/availableCars', {
        method: 'post',
        headers: {
          'Content-type': 'application/json'
        },
        body: JSON.stringify({ category, startDate, endDate })
      });
      const availableCars = await response.json();
      if (!response.ok)
        reject(availableCars);
      resolve(availableCars);
    } catch (e) {
      reject(e);
    }
  });
}

export function cancelRent(rentalID) {
  return new Promise(function (resolve, reject) {
    rentals = rentals.filter(r => r.id !== rentalID);
    resolve();
  });
}

export function rentalList() {
  return new Promise(async function (resolve, reject) {
    try {
      const response = await fetch('/api/rentals');
      const rentals = await response.json();
      if (!response.ok)
        reject(rentals);
      resolve(rentals.map((rental) => { return { ...rental, startDate: moment(rental.startDate, 'YYYY-MM-DD'), endDate: moment(rental.endDate, 'YYYY-MM-DD') } }));
    } catch (e) {
      reject(e);
    }
  });
}
export function isLogged() {
  return new Promise(async function (resolve, reject) {
    resolve(user)
  });
};
export function login(email, password) {
  return new Promise(async function (resolve, reject) {
    try {
      const response = await fetch('/api/login', {
        method: 'post',
        headers: {
          'Content-type': 'application/json'
        },
        body: JSON.stringify({ email, password })
      });
      const result = await response.json();
      if (!response.ok)
        reject(result.errors[0].msg);
      resolve(result);
    } catch (e) {
      reject(e);
    }
  });
};
export function logout() {
  return new Promise(async function (resolve, reject) {
    resolve()
  });
};
